chatStr = ""

def chat(query):
    global chatStr
    print(chatStr)
    openai.api_key = "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"
    chatStr += f"Gaurang: {query}\n Jarvis: "
    response = openai.Completion.create(
        model="text-davinci-003",
        prompt=chatStr,
        temperature=0.7,
        max_tokens=256,
        top_p=1,
        frequency_penalty=0,
        presence_penalty=0
    )
    speak(response["choices"][0]["text"])
    chatStr += f"{response['choices'][0]['text']}\n"
